import React from 'react'
import { StyleSheet, Text, View, SafeAreaView, ScrollView, Image, TouchableOpacity,ImageBackground, TouchableNativeFeedback } from 'react-native'
import { useNavigation } from '@react-navigation/native';

const initialScreen = () => {

    const navigation = useNavigation();

    const goSingUp = () => {
        console.log("Initial.Screen | success, navigating to Sing Up Screen");
        navigation.navigate("SingUp");
      }
    
    const goSingIn = () => {
        console.log("Initial.Screen | success, navigating to Sing In Screen");
        navigation.navigate("Login");
    }

    return (
    
    <ImageBackground source={require('../assets/cinemaSeats.jpg')} resizeMode="cover" style={styles.image}>
        
        <SafeAreaView style={ styles.container}>
        <ScrollView>
        <View style={ styles.container }>

            

                <View style={ styles.throwWrapper }> 
                    <Text style={[ styles.welcomeText, {fontFamily:'Inter_500Medium'}]}>Bem-vindo ao</Text>
                    <Text style={ styles.welcomeText}>Moovie Plus.</Text>

                    <Image source={require('../assets/icon.png')} style={ styles.imageCenter }></Image>

                    <Text style={styles.descriptionText}>Saiba tudo sobre os filmes que acabaram de sair no cinema com apenas um clique.</Text>
                    
                        
                    <View style={{ borderRadius: 12, overflow: "hidden"}}>
                        <TouchableNativeFeedback background={TouchableNativeFeedback.Ripple('#F54038')} onPress={goSingIn}>
                                
                            <View style={ styles.cardMessage }>
                                <Text style={ styles.buttonText }>Login</Text>
                            </View>

                        </TouchableNativeFeedback>
                    </View>

                    <View style={{ borderRadius: 12, overflow: "hidden", marginTop: 10}}>
                        <TouchableNativeFeedback background={TouchableNativeFeedback.Ripple('#F54038')} onPress={goSingIn}>
                                
                            <View style={ styles.cardMessage }>
                                <Text style={ styles.buttonText }>Cadastro</Text>
                            </View>

                        </TouchableNativeFeedback>
                    </View>
                    
                    {/*googleSubmitting && (

                        <TouchableOpacity style={ styles.button }>

                            <ActivityIndicator size='large' color={'#f5f5f5'}/>

                        </TouchableOpacity>        
                            
                    )*/}

            </View>

            

        </View>
        </ScrollView>
        </SafeAreaView>

        </ImageBackground>
    )
}

export default initialScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    image: {
        flex: 1,
        justifyContent: "center",
    },
    imageCenter: {
        width: '100%',
        height: 380,
        resizeMode: 'contain',
        marginTop: '5%',
    },
    throwWrapper: {
        paddingHorizontal: 30,
        paddingTop: '15%',
    },
    welcomeText: {
        fontFamily: 'Inter_600SemiBold',
        color: '#ffff',
        fontSize: 24,
    },
    descriptionText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#ffff',
        fontSize: 20,
        marginBottom: 40,
    },
    cardMessage:{
        flexDirection: 'row',
        height: 80,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#000',
    },
    buttonText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#f5f5f5',
        fontSize: 23,
    },
    registerText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#f5f5f5',
        fontSize: 16,
        marginBottom: '3%',
        marginTop: '3%',
    },
})
