import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View, SafeAreaView, ScrollView, TouchableOpacity, KeyboardAvoidingView, TextInput, TouchableNativeFeedback, Alert } from 'react-native'
import { StatusBar } from 'expo-status-bar';
import { useNavigation } from '@react-navigation/native';
import { auth } from '../firebase';

const loginScreen = () => {

    const navigation = useNavigation();

    const [email, SetEmail] = useState('')
    const [password, SetPassword] = useState('')

    const verifica = () => {
        if ((email, password) !== '') {
            handleSingIn()
        } else {
            Alert.alert(
                "Campos em branco",
                "Por favor, verifique se os campos foram preenchidos.",
            )
        }
    }


    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged(auth, (user) => {
            if (user) {
                navigation.replace('Home');
            } else {
                // User is signed out
                navigation.canGoBack() &&
                    navigation.popToTop();
            }
        });

        return unsubscribe
    })


    const SingIn = () => {
        auth.signInWithEmailAndPassword(email, password)
            .then((userCredential) => {
                // Signed in 
                const user = userCredential.user;
                navigation.replace('Home');
                console.log(user);
                
                // ...
            })
            .catch((error) => {
                const errorMessage = error.message;
                Alert.alert(
                    "Email/Senha incorretos",
                    errorMessage,
                )
                //"Por favor, verifique as informaÃ§Ãµes digitadas.",
            });
    }

    const goPassReset = () => {
        console.log("Initial.Screen | success, navigating to Sing");
        navigation.navigate("PassReset");
    }


    return (
        <KeyboardAvoidingView keyboardVerticalOffset={-280} style={styles.container} behavior="padding">

            <SafeAreaView>
                <ScrollView>

                    <View style={styles.HelpMeWrapper}>

                        <View style={styles.inputContainer}>

                            <Text style={styles.text}>Email</Text>
                            <TextInput placeholder="exemplo@email.com" selectionColor={'grey'} style={styles.input} value={email} onChangeText={text => SetEmail(text)}></TextInput>

                            <Text style={styles.text}>Senha</Text>
                            <TextInput placeholder="Digite sua senha" selectionColor={'grey'} style={styles.input} value={password} onChangeText={text => SetPassword(text)} secureTextEntry></TextInput>

                            <TouchableOpacity onPress={goPassReset} style={styles.esqueciButton}>

                                <Text style={styles.esqueciButton}>Esqueci minha senha.</Text>

                            </TouchableOpacity>

                        </View>

                        <View style={{ borderRadius: 40, overflow: "hidden", backgroundColor: '#F54038', marginTop: 30 }}>
                            <TouchableNativeFeedback background={TouchableNativeFeedback.Ripple('#1B2727')} onPress={SingIn}>

                                <View style={styles.button}>
                                    <Text style={styles.buttonText}>Login</Text>
                                </View>

                            </TouchableNativeFeedback>
                        </View>
                        <StatusBar style="light" />
                    </View>
                </ScrollView>
            </SafeAreaView>

        </KeyboardAvoidingView>
    )
}

export default loginScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#000'
    },
    HelpMeWrapper: {
        paddingTop: 10,
        paddingHorizontal: 20,
    },
    text: {
        fontSize: 25,
        padding: '2%',
        fontFamily: 'Inter_600SemiBold',
        color: '#ffff'
    },
    inputContainer: {
        width: '100%',
        marginTop: 150
    },
    esqueciButton: {
        fontSize: 16,
        fontFamily: "Inter_600SemiBold",
        color: '#ffff',
        marginTop: 8
    },
    input: {
        backgroundColor: '#d9d9d9',
        height: 70,
        borderRadius: 10,
        padding: 10,
        fontSize: 16,
        fontFamily: 'Inter_500Medium',
    },
    button: {
        flexDirection: 'row',
        height: 60,
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttonText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#f5f5f5',
        fontSize: 23,
    },
})
