import React from 'react'
import { StyleSheet, Text, View, SafeAreaView, ScrollView, Image, TouchableOpacity,ImageBackground, TouchableNativeFeedback } from 'react-native'
import { useNavigation } from '@react-navigation/native';

const initialScreen = () => {

    const navigation = useNavigation();

    const goSingUp = () => {
        console.log("Initial.Screen | success, navigating to Sing Up Screen");
        navigation.navigate("SingUp");
      }
    
    const goSingIn = () => {
        console.log("Initial.Screen | success, navigating to Sing In Screen");
        navigation.navigate("Login");
    }

    return (
    
    <ImageBackground source={require('../assets/cinemaSeats.jpg')} resizeMode="cover" style={styles.image}>
        
        <SafeAreaView style={ styles.container}>
        <ScrollView>
        <View style={ styles.container }>

            

                <View style={ styles.throwWrapper }> 
                    <Text style={[ styles.welcomeText, {fontFamily:'Inter_500Medium'}]}>Bem-vindo ao</Text>
                    <Text style={ styles.welcomeText}>Throw Message.</Text>


                    <Text style={styles.descriptionText}>Fale com qualquer pessoa do mundo de modo f치cil e instant칙neo.</Text>
                    
                    
                    
                    <TouchableNativeFeedback background={TouchableNativeFeedback.Ripple('#0a83fe')} onPress={goSingIn}>
                        <View styles={ styles.button }>
                            <Text style={ styles.buttonText }>Bora l치!</Text>
                        </View>
                    </TouchableNativeFeedback>
                    
                    
                    {/*googleSubmitting && (

                        <TouchableOpacity style={ styles.button }>

                            <ActivityIndicator size='large' color={'#f5f5f5'}/>

                        </TouchableOpacity>        
                            
                    )*/}
                    <View>
                        
                    <TouchableOpacity onPress={goSingUp}> 
                        <Text style={ styles.registerText }>Cadastre-se!</Text>
                    </TouchableOpacity>
                    </View>

            </View>

            

        </View>
        </ScrollView>
        </SafeAreaView>

        </ImageBackground>
    )
}

export default initialScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    image: {
        flex: 1,
        justifyContent: "center",
    },
    throwWrapper: {
        paddingHorizontal: 30,
        paddingTop: '15%',
    },
    welcomeText: {
        fontFamily: 'Inter_600SemiBold',
        color: '#ffff',
        fontSize: 24,
    },
    descriptionText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#ffff',
        fontSize: 20,
        marginBottom: 40,
    },
    button: {
        borderRadius: 8,
        width: '90%',
        height: '10%',
        paddingTop: 12,
        backgroundColor: '#f5f5f5',
        textAlign: 'center',
    },
    buttonText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#f5f5f5',
        fontSize: 23,
    },
    registerText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#f5f5f5',
        fontSize: 16,
        marginBottom: '3%',
        marginTop: '3%',
    },
})
