import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import {Inter_400Regular, Inter_500Medium, Inter_600SemiBold, useFonts} from '@expo-google-fonts/inter';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import initialScreen from './screens/initialScreen';
import loginScreen from './screens/loginScreen';
import registerScreen from './screens/registerScreen';
import passResetScreen from './screens/passResetScreen';
import homeScreen from './screens/homeScreen';
import favoritesScreen from './screens/favoritesScreen';

const Stack = createNativeStackNavigator();

export default function App() {

  const fonts = useFonts ({
    Inter_400Regular, 
    Inter_500Medium, 
    Inter_600SemiBold
  });


  if (!fonts){
    return <AppLoading />;
  }

  return (
  <View styles={styles.container}>
    <NavigationContainer>

      <Stack.Navigator>
        <Stack.Screen options={{ headerShown: false }} name="Initial" component={initialScreen}/>
        <Stack.Screen options={{ headerTitleAlign: 'center', headerStyle: {backgroundColor: '#000'}, headerTitleStyle: 'Inter_400Regular', headerTintColor: '#ffff'}} name="Login" component={loginScreen}/>
        <Stack.Screen options={{ headerTitleAlign: 'center', headerStyle: {backgroundColor: '#000'}, headerTitleStyle: 'Inter_400Regular', title: 'Cadastro', headerTintColor: '#ffff'}} name="Register" component={registerScreen}/>
        <Stack.Screen options={{ headerTitleAlign: 'center', headerStyle: {backgroundColor: '#000'}, headerTitleStyle: 'Inter_400Regular', title: 'Redefinição de Senha', headerTintColor: '#ffff'}} name="PassReset" component={passResetScreen}/>
        <Stack.Screen options={{}} name="Home" component={homeScreen}/>
        <Stack.Screen options={{}} name="Favorites" component={favoritesScreen}/>
      </Stack.Navigator>

    </NavigationContainer>
  </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#000'
  },
});
