import React from 'react'
import { StyleSheet, Text, View, SafeAreaView, ScrollView, Image, TouchableOpacity } from 'react-native'

const initialScreen = () => {

    const goSingUp = () => {
        console.log("Initial.Screen | success, navigating to Sing Up Screen");
        navigation.navigate("SingUp");
      }
    
    const goSingIn = () => {
        console.log("Initial.Screen | success, navigating to Sing In Screen");
        navigation.navigate("Login");
    }

    return (
        <SafeAreaView style={ styles.container}>
        <ScrollView>
        <View style={ styles.container }>
            <View style={ styles.throwWrapper }> 
                <Text style={[ styles.welcomeText, {fontFamily:'Inter_500Medium'}]}>Bem-vindo ao</Text>
                <Text style={ styles.welcomeText}>Throw Message.</Text>

                <Image source={require('../assets/cinemaSeats.jpg')} style={ styles.image }></Image>

                <Text style={styles.descriptionText}>Fale com qualquer pessoa do mundo de modo fácil e instantâneo.</Text>
                
                
                
                <TouchableOpacity style={ styles.button } onPress={goSingIn}>

                    <Text style={ styles.buttonText }>Bora lá!</Text>

                </TouchableOpacity>
                
                
                {/*googleSubmitting && (

                    <TouchableOpacity style={ styles.button }>

                        <ActivityIndicator size='large' color={'#f5f5f5'}/>

                    </TouchableOpacity>        
                        
                )*/}
            <View>
                <Text style={ styles.registerText }>Ainda não possiu login?</Text>
                
                <TouchableOpacity style={{color: '#063970'}} onPress={goSingUp}> 
                <Text style={ styles.registerText }>Cadastre-se!</Text>
                </TouchableOpacity>
            </View>
        </View>
        </View>
        </ScrollView>
        </SafeAreaView>
    )
}

export default initialScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#0f0f10',
    },
    throwWrapper: {
        paddingHorizontal: 30,
        paddingTop: '15%',
    },
    welcomeText: {
        fontFamily: 'Inter_600SemiBold',
        color: '#ffff',
        fontSize: 24,
    },
    descriptionText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#ffff',
        fontSize: 20,
        marginBottom: 40,
    },
    image: {
        width: '100%',
        height: 400,
        resizeMode: 'contain',
        marginTop: '5%',
    },
    button: {
        borderRadius: 8,
        paddingBottom: 12,
        paddingTop: 12,
        backgroundColor: '#0a83fe',
        textAlign: 'center',
    },
    buttonText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#f5f5f5',
        fontSize: 23,
    },
    registerText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#f5f5f5',
        fontSize: 16,
        marginBottom: '3%',
        marginTop: '3%',
    },
})
