import React from 'react'
import { StyleSheet, Text, View, SafeAreaView, ScrollView, TouchableOpacity, KeyboardAvoidingView, TextInput,TouchableNativeFeedback } from 'react-native'
import { useState, useEffect } from 'react';
import { StatusBar } from 'expo-status-bar';
import { useNavigation } from '@react-navigation/native';
import { auth } from '../firebase';

const loginScreen = () => {

    const navigation = useNavigation();

    const [email, SetEmail] = useState('')
    const [password, SetPassword] = useState('')

    const verifica = () =>{
        if ((email, password) !== '' ) {
            handleSingIn()
            } else{
                Alert.alert(
                "Oops!",
                "Os campos não foram preenchidos.",
                )
            }
    }

    const makeLogin = () => {
        console.log("Initial.Screen | success, navigating to Sing In Screen");
        navigation.navigate("Home");
    }
    
    return (
        <KeyboardAvoidingView keyboardVerticalOffset = { -600 } style={ styles.container } behavior="padding">

        <SafeAreaView>
        <ScrollView>

        <View style={ styles.HelpMeWrapper }>

            <View>
          
                <Text style={ styles.sectionText}>Preencha as informações abaixo para realizar o login.</Text>

            </View>

            <View style={ styles.inputContainer }>

                <Text style={ styles.text }>Email</Text>
                <TextInput placeholder="exemplo@email.com" style={ styles.input } value={email} onChangeText={text => SetEmail(text)}></TextInput>

                <Text style={ styles.text }>Senha</Text>
                <TextInput placeholder="Digite sua senha" style={ styles.input } value={password} onChangeText={text => SetPassword(text)} secureTextEntry></TextInput>

                <TouchableOpacity onPress={() => {
                try {
                    navigation.navigate('ResetPassword');
                } catch (err) {
                    console.error(err);
                }}} style={ styles.esqueciButton }>

                    <Text style={ styles.esqueciButton }>Esqueci minha senha.</Text>

                </TouchableOpacity>
            
            </View>
        
            <View style={{ borderRadius: 40, overflow: "hidden", backgroundColor: '#1B2727', marginTop: 30}}>
                        <TouchableNativeFeedback background={TouchableNativeFeedback.Ripple('#F54038')} onPress={makeLogin}>
                                
                            <View style={ styles.button }>
                                <Text style={ styles.buttonText }>Login</Text>
                            </View>

                        </TouchableNativeFeedback>
                    </View>
            <StatusBar style="light" />
        </View>
        </ScrollView>
        </SafeAreaView>

        </KeyboardAvoidingView>
    )
}

export default loginScreen

const styles = StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: '#000'
    },
    HelpMeWrapper:{
        paddingTop: 10,
        paddingHorizontal: 20,
    },
    sectionText:{
        fontSize: 20,
        textAlign: 'justify',
        fontFamily: 'Inter_400Regular',
        color: '#ffff'
    },
    text:{
        fontSize: 25,
        padding: '2%',
        fontFamily: 'Inter_600SemiBold',
        color: '#ffff'
    },
    inputContainer:{
        width:'100%',
        marginTop: 150  
    },
    esqueciButton:{
        fontSize: 16,
        fontFamily: "Inter_600SemiBold",
        color: '#ffff',
        marginTop: 8
    },
    input:{
        backgroundColor: '#d9d9d9',
        height: 70,
        borderRadius: 10,
        padding: 10,
        fontSize: 16,
        fontFamily: 'Inter_500Medium',
    },
    buttonContainer:{
        width: '100%',
        marginTop: 40,
        justifyContent: 'center',
    },
    button:{
        flexDirection: 'row',
        height: 60,
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttonText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#f5f5f5',
        fontSize: 23,
    },
})
