import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import {Inter_400Regular, Inter_500Medium, Inter_600SemiBold, useFonts} from '@expo-google-fonts/inter';
import initialScreen from './screens/initialScreen';
import loginScreen from './screens/loginScreen';
import registerScreen from './screens/registerScreen';
import passResetScreen from './screens/passResetScreen';
import homeScreen from './screens/homeScreen';
import favoritesScreen from './screens/favoritesScreen';

const Stack = createNativeStackNavigator();
const Tab = createBottomTabNavigator();

export default function App() {

  const fonts = useFonts ({
    Inter_400Regular, 
    Inter_500Medium, 
    Inter_600SemiBold
  });


  if (!fonts){
    return <AppLoading />;
  }

  return (

    <NavigationContainer>

      <Stack.Navigator>
        <Stack.Screen options={{headerBackVisible: false, headerTitleAlign: 'center', headerStyle: {backgroundColor: 'transparent'}, headerTintColor: '#f5f5f5', headerTitleStyle: 'Inter_400Regular'}} name="Initial" component={initialScreen}/>
        <Stack.Screen options={{}} name="Login" component={loginScreen}/>
        <Stack.Screen options={{}} name="Register" component={registerScreen}/>
        <Stack.Screen options={{}} name="PassReset" component={passResetScreen}/>
        <Stack.Screen options={{}} name="Home" component={homeScreen}/>
        <Stack.Screen options={{}} name="Favorites" component={favoritesScreen}/>
      </Stack.Navigator>

    </NavigationContainer>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
