import React from 'react'
import { StyleSheet, Text, TextInput, View, Alert, KeyboardAvoidingView, SafeAreaView, ScrollView, TouchableNativeFeedback } from 'react-native'
import { useState } from 'react';
//import { initializeApp } from 'firebase/app';
import { useNavigation } from '@react-navigation/native';

const passResetScreen = () => {
    

    const navigation = useNavigation();

    const [email, SetEmail] = useState('')

    
    const verifica = () =>{
        if ((email) !== '' ) {
            forgotPassword()
            } else{
                Alert.alert(
                "",
                "Por favor, verifique se o campo email foi preenchido.",
                )
            }
        }

    //redefinir Senha
    async function forgotPassword(){
        //redefinir Senha
        await firebase.auth().sendPasswordResetEmail(email)
            .then(() => {
                Alert.alert(
                "Redefinição enviada!",
                "Por favor, verifique o seu email.",
                );
            {
                try {
                    navigation.navigate('Home');
                } catch (err) {
                    console.error(err);
            }}
            })
        .catch((error) => {
            alert(error.message)
            var errorCode = error.code;
            var errorMessage = error.message;
            console.log(errorCode);
            console.log(errorMessage);
        });
    }

    return (
        <KeyboardAvoidingView keyboardVerticalOffset = { -280 } style={ styles.container } behavior="padding">

        <SafeAreaView>
        <ScrollView>

        <View style={ styles.HelpMeWrapper }>

            <View style={ styles.inputContainer }>

                <Text style={ styles.text }>Email</Text>
                <TextInput placeholder="exemplo@email.com" style={ styles.input } value={email} onChangeText={text => SetEmail(text)}></TextInput>
            </View>
        

        <View style={{ borderRadius: 40, overflow: "hidden", backgroundColor: '#1B2727', marginTop: 35}}>
                        <TouchableNativeFeedback background={TouchableNativeFeedback.Ripple('#F54038')} onPress={verifica}>
                                
                            <View style={ styles.button }>
                                <Text style={ styles.buttonText }>Login</Text>
                            </View>

                        </TouchableNativeFeedback>
                    </View>
        </View>
        </ScrollView>
        </SafeAreaView>

        </KeyboardAvoidingView>
    )
}

export default passResetScreen

const styles = StyleSheet.create({
    container:{
        flex: 1,
    },
    HelpMeWrapper:{
        paddingTop: 80,
        paddingHorizontal: 20,
    },
    sectionTitle:{
        fontSize: 24,
        fontFamily: "Inter_600SemiBold",
    },
    sectionText:{
        fontSize: 20,
        fontFamily: 'Inter_500Medium',
    },
    text:{
        fontSize: 25,
        padding: '2%',
        fontFamily: 'Inter_600SemiBold',
    },
    inputContainer:{
        width:'100%',
        marginTop: 150  
    },
    input:{
        backgroundColor: '#d9d9d9',
        height: 70,
        borderRadius: 10,
        padding: 10,
        fontSize: 16,
        fontFamily: 'Inter_500Medium',
    },
    button:{
        flexDirection: 'row',
        height: 60,
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttonText: {
        fontFamily: 'Inter_500Medium',
        textAlign: 'center',
        color: '#f5f5f5',
        fontSize: 23,
    },
})
